import React from "react";

const e = React.createElement;
const consoleStyle = {
  width: "100%",
  height: 510,
  resize: "none",
  outline: "none"
};

class Console extends React.Component {

  componentDidMount(){
      this.setState(
         {console:this.props.console }
      )
  }
  componentWillReceiveProps(props){
      console.log('props received',props);
      this.setState({
          console:this.props.console
      })
  }  
  renderLines() {
    return this.state.console.join("\n");
  }

  render() {
    return e("textarea", {
      style: consoleStyle,
      defaultValue: this.renderLines()
    });
  }
}

export default Console;
