import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import * as tf from "@tensorflow/tfjs";
import dataset from "./Dataset";
import Console from "./Components/Console";
import Button from "./Components/Button";
import RadioButton from "./Components/RadioButton";
import Input from "./Components/Input";

const e = React.createElement;
class App extends React.Component {
  constructor(props) {
    super(props);
    this.clearConsole = this.clearConsole.bind(this);
    this.addConsoleValue = this.addConsoleValue.bind(this);
    this.trainingModeAction = this.trainingModeAction.bind(this);
    this.updateEpochs = this.updateEpochs.bind(this);
    this.updateLearningRate = this.updateLearningRate.bind(this);
    this.updatebatchIterations = this.updatebatchIterations.bind(this);
    this.train = this.train.bind(this);
    this.state = {
      console: [],
      epochs: 50,
      learningRate:1.5,
      batchIterations: 1,
      mode: "default",
      trainedModel: []
    };
  }

  clearConsole() {
    this.setState({
      console: []
    });
  }

  train() {
    const date = new Date();
    this.setState({
      console: [
        ...this.state.console,
        "[" +
          date.toLocaleTimeString() +
          "]:Epochs " +
          this.state.epochs +
          "Iter " +
          this.state.batchIterations
      ]
    });
  }

  updatebatchIterations(iteration) {
    this.setState({
      batchIterations:iteration
    });
  }
  updateLearningRate(learningRate) {
    this.setState({
      learningRate:learningRate
    });
  }
  updateEpochs(epoch) {
    this.setState({
      epochs: epoch
    });
  }

  trainingModeAction(mode) {
    this.setState({
      mode: mode
    });
  }

  addConsoleValue(value) {
    const date = new Date();
    this.setState({
      console: [
        ...this.state.console,
        "[" + date.toLocaleTimeString() + "] " + value
      ]
    });
  }

  renderMode(mode) {
    switch (mode) {
      default:
        return e(
          "div",
          {},
          <Button label="Train with default dataset" onClick={this.test} />,
          <Button label="Predict with test dataset" />,
          <Button label="Predict with pre trained modelt" />
        );
      case "custom":
        return e("p", {}, "custom goest here...");
    }
  }

  test = async () => {
    this.addConsoleValue("Training...");

    //Create model as sequential

    const model = tf.sequential();

    const inputLayer = tf.layers.dense({
      units: 16,
      inputDim: 30,
      activation: "relu"
    });

    const hiddenLayer = tf.layers.dense({
      units: 8,
      activation: "relu"
    });

    const hiddenLayer2 = tf.layers.dense({
      units: 8,
      activation: "relu"
    });

    const outputLayer = tf.layers.dense({
      units: 1,
      activation: "sigmoid"
    });

    model.add(inputLayer);
    model.add(hiddenLayer);
    model.add(hiddenLayer2);
    model.add(outputLayer);

    model.compile({
      optimizer: "rmsprop",
      loss: "meanSquaredError",
      metrics: ["accuracy"]
    });

    const xTrain = tf.tensor2d(
      dataset.xTrain.map(item => [
        item[0],
        item[1],
        item[2],
        item[3],
        item[4],
        item[5],
        item[6],
        item[7],
        item[8],
        item[9],
        item[10],
        item[11],
        item[12],
        item[13],
        item[14],
        item[15],
        item[16],
        item[17],
        item[18],
        item[19],
        item[20],
        item[21],
        item[22],
        item[23],
        item[24],
        item[25],
        item[26],
        item[27],
        item[28],
        item[29]
      ]),
      [454, 30]
    );

    const yTrain = tf.tensor2d(dataset.yTrain.map(item => [item[0]]), [454, 1]);

    const xTest = tf.tensor2d(
      dataset.xTest.map(item => [
        item[0],
        item[1],
        item[2],
        item[3],
        item[4],
        item[5],
        item[6],
        item[7],
        item[8],
        item[9],
        item[10],
        item[11],
        item[12],
        item[13],
        item[14],
        item[15],
        item[16],
        item[17],
        item[18],
        item[19],
        item[20],
        item[21],
        item[22],
        item[23],
        item[24],
        item[25],
        item[26],
        item[27],
        item[28],
        item[29]
      ]),
      [114, 30]
    );

    const yTest = tf.tensor2d(dataset.yTest.map(item => [item[0]]), [114, 1]);
    let trainBatchCount = 0;
    let valAcc;
    let onIteration = 0;
    const batchSize = this.state.batchIterations || 0;
    const trainEpochs = this.state.epochs || 0;
    const validationSplit = 0.15; //learning rate
    console.log(dataset.xTrain[0],validationSplit,batchSize,trainEpochs);
    const totalNumBatches = Math.ceil((xTrain[0] * (1 - validationSplit)) / batchSize) * trainEpochs;
    const optimizer = tf.train.sgd(0.1);
    // const totalTraining = (trainBatchCount / totalNumBatches * 100).toFixed(1);
            this.addConsoleValue(
              `Total training =${totalNumBatches}`
            );
    await model
      .fit(xTrain, yTrain, {
        batchSize: this.state.batchIterations,
        epochs: this.state.epochs,
        callbacks: {
          onBatchEnd: async (batch, logs) => {
            // trainBatchCount++;
            // // console.table(logs);

            // this.addConsoleValue(
            //   `Batch =${trainBatchCount} Loss=${logs.loss}  Acc=${logs.acc}`
            // );
            // if (trainBatchCount === batchSize) {
            //   // console.table(logs);
            //   // this.addConsoleValue(
            //   //   `On batch end, Batch=${batch}, Log loss=${logs.loss}  acc=${logs.acc}`
            //   // );
            //   // trainBatchCount = 0;
            // }
          },
          onEpochEnd: async (epoch, logs) => {
            valAcc = logs.acc;
            // console.table(logs);
            this.addConsoleValue(
              `Epoch =${epoch} Loss=${logs.loss}  Acc=${logs.acc}`
            );
            // if (onIteration) {
            //   // this.addConsoleValue(`On epoch end, ${epoch}, ${logs}`);
            // }
          }
        }
      })
      .then(() => {
        const testResult = model.evaluate(xTest, yTest);
        // console.table(testResult);

        const testAccPercent = testResult[1].dataSync()[0] * 100;
        const finalValAccPercent = valAcc * 100;
        this.addConsoleValue(
          `Final validation accuracy: ${finalValAccPercent.toFixed(1)}%; ` +
            `Final test accuracy: ${testAccPercent.toFixed(1)}%`
        );

       const output = model.predict(yTest);
       console.table(output)
      //  const predictions = Array.from(output.argMax(1).dataSync());
      //  console.table(output);
      //  console.table(predictions);
      });
    // console.log(`Iteration ${i}: ${res.history.loss}`);
    // }

    // console.log('finisheeeed,predicting');
    // model.predict(xTest,yTest).print();
    // await model.save('downloads://my-model');

    // const json = fetch('./trained_alex.json');
    // // const model2 = await tf.loadLayersModel('file:trained_alex.json');
    // console.log('model2',json);
  };

  render() {
    return e(
      "div",
      {
        className: "container",
        style: {
          position: "relative",
          top: 100
        }
      },
      e(
        "div",
        { className: "row" },
        e(
          "div",
          { className: "col-md-6" },
          e("p", null, "Console"),
          <Console console={this.state.console} />,
          <Button label="Clear" onClick={this.clearConsole} />
        ),
        e(
          "div",
          { className: "col-md-6" },
          e("p", {}, "Train"),
          e("p", null, "Default Breast Cancer Dataset"),
          e(
            "div",
            { className: "row" },
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Epochs"),
              <Input
                placeholder="Enter here number of epochs"
                value={this.state.epochs}
                updateValue={this.updateEpochs}
              />
            ),
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Batch Iterations"),
              <Input
                placeholder="Enter here number of batchIterations"
                value={this.state.batchIterations}
                updateValue={this.updatebatchIterations}
              />
            ),
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Learning rate"),
              <Input
                placeholder="Learning rate"
                value={this.state.learningRate}
                updateValue={this.updateLearningRate}
              />
            ),
            e(
              "div",
              { className: "col-md-6" },
              <RadioButton
                label="Default dataset train"
                value="default"
                onModeSelection={this.trainingModeAction}
              />
            ),
            e(
              "div",
              { className: "col-md-6" },
              <RadioButton
                label="Custom dataset train"
                value="custom"
                onModeSelection={this.trainingModeAction}
              />
            )
          ),
          this.renderMode(this.state.mode)
        )
      )
    );
  }
}

export default App;
