import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import * as tf from "@tensorflow/tfjs";
import Console from "./Components/Console";
import Button from "./Components/Button";
import RadioButton from "./Components/RadioButton";
import Input from "./Components/Input";
import data from "./dataset";

const e = React.createElement;
const modelStorage = async () => {
  return await tf.loadLayersModel("localstorage://breast-cancer-model");
};
class App extends React.Component {
  constructor(props) {
    super(props);
    this.clearConsole = this.clearConsole.bind(this);
    this.addConsoleValue = this.addConsoleValue.bind(this);
    this.trainingModeAction = this.trainingModeAction.bind(this);
    this.updateEpochs = this.updateEpochs.bind(this);
    this.updateLearningRate = this.updateLearningRate.bind(this);
    this.updatebatchIterations = this.updatebatchIterations.bind(this);
    this.predictTrainedModel = this.predictTrainedModel.bind(this);
    this.train = this.train.bind(this);
    this.state = {
      console: [],
      epochs: 100,
      learningRate: 1.5,
      batchIterations: 1,
      mode: "default"
    };
  }

  clearConsole() {
    this.setState({
      console: []
    });
  }

  train() {
    const date = new Date();
    this.setState({
      console: [
        ...this.state.console,
        "[" +
          date.toLocaleTimeString() +
          "]:Epochs " +
          this.state.epochs +
          "Iter " +
          this.state.batchIterations
      ]
    });
  }

  updatebatchIterations(iteration) {
    this.setState({
      batchIterations: iteration
    });
  }
  updateLearningRate(learningRate) {
    this.setState({
      learningRate: learningRate
    });
  }
  updateEpochs(epoch) {
    this.setState({
      epochs: epoch
    });
  }

  trainingModeAction(mode) {
    this.setState({
      mode: mode
    });
  }

  addConsoleValue(value) {
    const date = new Date();
    this.setState({
      console: [
        ...this.state.console,
        "[" + date.toLocaleTimeString() + "] " + value
      ]
    });
  }

  renderMode(mode) {
    switch (mode) {
      default:
        return e(
          "div",
          {},
          <Button label="Train with default dataset" onClick={this.test} />,
          <Button label="Predict with test dataset" />,
          <Button
            label="Predict with pre trained model"
            onClick={this.predictTrainedModel}
          />,
          <Button label="Download model" />
        );
      case "custom":
        return e("p", {}, "custom goest here...");
    }
  }
  predictTrainedModel = async () => {
    const xTest = tf.tensor2d(data.xTest, [data.xTest.length, 30]);
    const yTest = tf.tensor2d(data.yTest, [data.yTest.length, 1]);
    let correct = 0;
    let wrong = 0;
    const modelStorage = await tf.loadLayersModel(
      "localstorage://breast-cancer-model"
    );

    tf.tidy(() => {
      const output = modelStorage.predict(xTest);

      // const axis = 1;
      // const labels = Array.from(yTest.argMax(axis).dataSync());
      // const predictions = Array.from(output.argMax(axis).dataSync();
      const predictions = output.dataSync();
      let correct = 0;
      let wrong = 0;
      let total = 0;
      // console.log(predictions);
      // console.log('ytest',yTest.);

      const yTestData = yTest.dataSync();
      console.log("realytest", yTestData);
      console.log("pred=d", predictions);
      predictions.map((value, index) => {
        total++;

        console.log(value, yTestData[index]);

        if (yTestData[index] === 1) {
          // console.log('value=',value,'ytest=',ytestData)
          const fromTest = value >= 0.5 ? "Correct" : "Wrong";
          if (fromTest === "Correct") {
            correct++;
          } else {
            wrong++;
          }
          this.addConsoleValue(
            `[${index}] = Malignant, Dataset Test = ${fromTest}`
          );
        } else {
          const fromTest = value <= 0.5 ? "Correct" : "Wrong";
          if (fromTest === "Correct") {
            correct++;
          } else {
            wrong++;
          }
          this.addConsoleValue(
            `[${index}] = Benign, Dataset Test= ${fromTest} `
          );
        }
      });

      this.addConsoleValue(
        `Correct=${correct}, Wrong = ${wrong} Total=${total}`
      );
      // console.log('labels',labels,'predictions',predictions);
    });
  };
  test = async () => {
    this.addConsoleValue("Training...");

    const dataset = data.dataset;
    const yTrain = [];
    dataset.map(row => {
      //We do not need ID so we will delete ID from array
      delete row.id;
      //test data using target M=1, B =0;
      yTrain.push([row.diagnosis === "M" ? 1 : 0]);
      delete row.diagnosis;
      return row;
    });
    const xTrain = dataset.map(row => [
      row.radius_mean,
      row.texture_mean,
      row.perimeter_mean,
      row.area_mean,
      row.smoothness_mean,
      row.compactness_mean,
      row.concavity_mean,
      row.concave_points_mean,
      row.symmetry_mean,
      row.fractal_dimension_mean,
      row.radius_se,
      row.texture_se,
      row.perimeter_se,
      row.area_se,
      row.smoothness_se,
      row.compactness_se,
      row.concavity_se,
      row.concave_points_se,
      row.symmetry_se,
      row.fractal_dimension_se,
      row.radius_worst,
      row.texture_worst,
      row.perimeter_worst,
      row.area_worst,
      row.smoothness_worst,
      row.compactness_worst,
      row.concavity_worst,
      row.concave_points_worst,
      row.symmetry_worst,
      row.fractal_dimension_worst
    ]);

    console.log(xTrain);
    const xTensorTrain = tf.tensor2d(xTrain, [xTrain.length, xTrain[0].length]);
    const yTensorTrain = tf.tensor2d(yTrain, [yTrain.length, yTrain[0].length]);


    //Create model as sequential

    const model = tf.sequential();
    const learningRate = this.state.learningRate;
    const optimizer = tf.train.sgd(learningRate);
    const inputLayer = tf.layers.dense({
      units: 16,
      inputDim: 30,
      activation: "relu"
    });

    const hiddenLayer = tf.layers.dense({
      units: 8,
      activation: "relu"
    });

    const hiddenLayer2 = tf.layers.dense({
      units: 8,
      activation: "relu"
    });
    const outputLayer = tf.layers.dense({
      units: 1,
      activation: "sigmoid"
    });

    model.add(inputLayer);
    model.add(hiddenLayer);
    model.add(hiddenLayer2);
    // model.add(hiddenLayer3);
    model.add(outputLayer);

    model.compile({
      optimizer: "rmsprop", //learning rate
      loss: "meanSquaredError",
      metrics: ["accuracy"]
    });

    const xTest = tf.tensor2d(data.xTest, [data.xTest.length, 30]);
    const yTest = tf.tensor2d(data.yTest, [data.yTest.length, 1]);
    const batchSize = this.state.batchIterations || 0;
    const trainEpochs = this.state.epochs || 0;
    const validationSplit = 0.15; //learning rate
    // const totalNumBatches = Math.ceil(xTensorTrain.shape[0] * (1 - validationSplit) / batchSize) *trainEpochs;
    let valAcc;
    await model
      .fit(xTensorTrain, yTensorTrain, {
        batchSize: batchSize,
        epochs: trainEpochs,
        validationData: [xTest, yTest],
        callbacks: {
          onEpochEnd: async (epoch, logs) => {
            valAcc = logs.acc;
            this.addConsoleValue(
              `Epoch =${epoch} Loss=${logs.loss}  Acc=${logs.acc}`
            );
          }
        }
      })
      .then(async () => {
        const testResult = model.evaluate(xTensorTrain, yTensorTrain);
        const testAccPercent = testResult[0].dataSync()[0] * 100;
        const finalValAccPercent = valAcc * 100;
        this.addConsoleValue(
          `Final validation accuracy: ${finalValAccPercent.toFixed(1)}%; ` +
            `Final test accuracy: ${testAccPercent.toFixed(1)}%`
        );

        await model.save("localstorage://breast-cancer-model");
        this.addConsoleValue("Model savet to localStorage");
      });
  };

  render() {
    return e(
      "div",
      {
        className: "container",
        style: {
          position: "relative",
          top: 100
        }
      },
      e(
        "div",
        { className: "row" },
        e(
          "div",
          { className: "col-md-6" },
          e("p", null, "Console"),
          <Console console={this.state.console} />,
          <Button label="Clear" onClick={this.clearConsole} />
        ),
        e(
          "div",
          { className: "col-md-6" },
          e("p", {}, "Train"),
          e("p", null, "Default Breast Cancer Dataset"),
          e(
            "div",
            { className: "row" },
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Epochs"),
              <Input
                placeholder="Enter here number of epochs"
                value={this.state.epochs}
                updateValue={this.updateEpochs}
              />
            ),
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Batch Iterations"),
              <Input
                placeholder="Enter here number of batchIterations"
                value={this.state.batchIterations}
                updateValue={this.updatebatchIterations}
              />
            ),
            e(
              "div",
              { className: "col-md-4" },
              e("p", {}, "Learning rate"),
              <Input
                placeholder="Learning rate"
                value={this.state.learningRate}
                updateValue={this.updateLearningRate}
              />
            ),
            e(
              "div",
              { className: "col-md-6" },
              <RadioButton
                label="Default dataset train"
                value="default"
                onModeSelection={this.trainingModeAction}
              />
            ),
            e(
              "div",
              { className: "col-md-6" },
              <RadioButton
                label="Custom dataset train"
                value="custom"
                onModeSelection={this.trainingModeAction}
              />
            )
          ),
          this.renderMode(this.state.mode)
        )
      )
    );
  }
}

export default App;
